import { useState } from 'react'
import './Book.css'

function Book(props) {

    let [titleContent, setTitleContent] = useState(<span id='container-data2' className="book-data">{props.title}</span>)
    let [authorContent, setAuthorContent] = useState(<span id='container-data3' className="book-data">{props.author}</span>)
    let [priceContent, setPriceContent] = useState(<span id='container-data4' className="book-data">{props.price}</span>)

    function handleUpdate(a) {
        console.log(a);
        setTitleContent(<input type="text" className="input-data" id='input-data2' value={props.title}/>)
        setAuthorContent(<input type="text" className="input-data" id='input-data3' value={props.author}/>)
        setPriceContent(<input type="text" className="input-data" id='input-data4' value={props.price}/>)
    }
    return (
        <div className="book-container">
            <span id='container-data1' className="book-data">{props.id}</span>
            {titleContent}
            {authorContent}
            {priceContent}

            <button id='btnRemove' onClick={() => { props.handleRemove(props.id) }}>Remove</button>
            <button id='btnUpdate' onClick={()=>{handleUpdate(props.id)}}>Update</button>
        </div>

    )
}

export default Book;
