      title: "Think and Grow Rich",
      author: "Nepolian Hill",
      price: "RS.199"
    },
    {
      id: 2,
      title: "ShivCharitra",
      author: "Purushottam Khedekar",
      price: "RS.149"
    },
    {
      id: 3,
      title: "Srimad Bhagavad Gita As It Is : Marathi",
      author: "Swami Prabhupada",
      price: "RS.249"
    },
    {
      id: 4,
      title: "Yayati",
      author: "V. S. Khandekar",
      price: "RS.299"
    }
  ] */

  function handleRemove(id) {
    let newBooks = books.filter(
      (element) => {
        return element.id !== id;
      }
    )
    setBooks(newBooks);
  }

  function handleSubmit(event) {
    event.preventDefault();
      let flag = 0;
     books.forEach(
       (element) => {
         if (element.id === parseInt(enteredId)) {
           return flag = 1;
         }
       }
     )
     if (flag === 0) {
       let book = {
         id: enteredId,
         title: enteredTitle,
         author: enteredAuthor,
         price: enteredPrice
       }
        let newBooks = [...books]
       newBooks.push(book);
       setBooks(newBooks); 
     } 

  /*   let book = {
      id: enteredId,
      title: enteredTitle,
      author: enteredAuthor,
      price: enteredPrice
    }
    fetch('http://localhost:8000/books', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(book)
    })
      .then((book) => {
        let newBooks = [...books]
        newBooks.push(book);
        setBooks(newBooks);
      })
 */

  }

  function updateData() {

  }

  return (
    <div className='main-container'>
      <div className='top-section'>
        <img src="book-logo.png" alt="Logo" />
        <h1 className='heading'>Book Management System</h1>

      </div>
      <AddBook handleSubmit={handleSubmit} setEnteredId={setEnteredId} setEnteredTitle={setEnteredTitle} setEnteredAuthor={setEnteredAuthor} setEnteredPrice={setEnteredPrice} />
      {
        books && books.map((element) => {
          return <Book id={element.id} title={element.title} author={element.author} price={element.price} handleRemove={handleRemove} updateData={updateData} books={books} setBooks={setBooks} />
        })
      }
    </div>
  );
}

export default App;
