import { useState } from 'react'
import './Book.css'

function Book(props) {
    let [titleContent, setTitleContent] =useState (<span id='container-data1' className="book-data">{props.title}</span>)
    let [authorContent, setAuthorContent] = (<span id='container-data3' className="book-data">{props.author}</span>)
    let [priceContent, setPriceContent] = (<span id='container-data4' className="book-data">{props.price}</span>)
   
   
    return (
        <div className="book-container">
            <span id='container-data1' className="book-data">{props.id}</span>
            {titleContent}
            {authorContent}
            {priceContent}
           
            <button id='btnRemove' onClick={() => { props.handleRemove(props.id) }}>Remove</button>
            <button id='btnUpdate'>Update</button>
        </div>

    )
}

export default Book;
