import './AddBook.css'

function AddBook(props) {
    function handleChangeId(event) {
        props.setEnteredId(event.target.value);
        fetch('http://localhost:8000/books')
            .then((response) => {
                return response.json();
            })
            .then((book) => {
                book.filter(
                    (element) => {
                        if (event.target.value == element.id) {
                            props.setEnteredId("");
                            return alert("Please entered another Id. This id already in used");
                        }
                    }
                )
            })
    }

    function handleChangeTitle(event) {
        props.setEnteredTitle(event.target.value);
    }

    function handleChangeAuthor(event) {
        props.setEnteredAuthor(event.target.value);
    }

    function handleChangePrice(event) {
        props.setEnteredPrice(event.target.value);
    }

    return (
        <div className="form-container">
            <form className='form-div' onSubmit={props.handleSubmit}>
                <span id='span1'>
                    <label >Id: </label>
                    <input type="text" id='textId' onChange={handleChangeId} className='enteredInput' value={props.enteredId} re/>
                </span>
                <span id='span2'>
                    <label>Title: </label>
                    <input type="text" id='textTitle' onChange={handleChangeTitle} className='enteredInput' value={props.enteredTitle} />
                </span>
                <span id='span3'>
                    <label> Author: </label>
                    <input type="text" id='textAuthor' onChange={handleChangeAuthor} className='enteredInput' value={props.enteredAuthor} />
                </span>
                <span id='span4'>
                    <label>Price: </label>
                    <input type="text" id='textPrice' onChange={handleChangePrice} className='enteredInput' value={props.enteredPrice} />
                </span>
                <button type="submit" id='btnAdd' >Add</button>
            </form>
        </div>
    )
}

export default AddBook
