import { useState } from 'react';
import './App.css';
import Book from './components/Book';
import AddBook from './components/AddBook';

function App() {
  let [enteredId, setEnteredId] = useState("");
  let [enteredTitle, setEnteredTitle] = useState("");
  let [enteredAuthor, setEnteredAuthor] = useState("");
  let [enteredPrice, setEnteredPrice] = useState("");

  let [books, setBooks] = useState([
    {
      id: 1,
      title: "Think and Grow Rich",
      author: "Nepolian Hill",
      price: "RS.199"
    },
    {
      id: 2,
      title: "ShivCharitra",
      author: "Purushottam Khedekar",
      price: "RS.149"
    },
    {
      id: 3,
      title: "Srimad Bhagavad Gita As It Is : Marathi",
      author: "Swami Prabhupada",
      price: "RS.249"
    },
    {
      id: 4,
      title: "Yayati",
      author: "V. S. Khandekar",
      price: "RS.299"
    },
  ])

  function handleRemove(id) {
    let newBooks = books.filter(
      (element) => {
        return element.id !== id;
      }
    )
    setBooks(newBooks);
  }

  function handleSubmit(event) {
    event.preventDefault();
    let flag = 0;
   books.forEach(
      (element) => {
        if (element.id === parseInt(enteredId)) {
          return flag = 1;
        }
      }
    )
    if (flag === 0) {
      let book = {
        id: enteredId,
        title: enteredTitle,
        author: enteredAuthor,
        price: enteredPrice
      }
      let newBooks = [...books]
      newBooks.push(book);
      setBooks(newBooks);
    }
  }

  return (
    <div className='main-container'>
    <div>
    <h1 className='heading'>Book Management System</h1>

    </div>
      <AddBook handleSubmit={handleSubmit} setEnteredId={setEnteredId} setEnteredTitle={setEnteredTitle} setEnteredAuthor={setEnteredAuthor} setEnteredPrice={setEnteredPrice} />
      {
        books.map((element) => {
          return <Book id={element.id} title={element.title} author={element.author} price={element.price} handleRemove={handleRemove} />
        })
      }
    </div>
  );
}

export default App;
