import { useEffect, useState } from 'react';
import './App.css';
import Book from './components/Book';
import AddBook from './components/AddBook';

function App() {
  let [enteredId, setEnteredId] = useState("");
  let [enteredTitle, setEnteredTitle] = useState("");
  let [enteredAuthor, setEnteredAuthor] = useState("");
  let [enteredPrice, setEnteredPrice] = useState("");

  let [books, setBooks] = useState(null);

  useEffect(() => {
    fetch('http://localhost:8000/books')
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setBooks(data)
      })
  })

  function handleRemove(id) {
    /* let newBooks = books.filter(
      (element) => {
        return element.id !== id;
      }
    )
    setBooks(newBooks); */

    fetch(`http://localhost:8000/books/${id}`, {
      method: 'DELETE'
    })
      .then(() => {
        let newBooks = books.filter(
          (element) => {
            return element.id !== id;
          }
        )
        setBooks(newBooks);
      })
  }

  function handleSubmit(event) {
    event.preventDefault();
    /*      let flag = 0;
        books.forEach(
          (element) => {
            if (element.id === parseInt(enteredId)) {
              return flag = 1;
            }
          }
        )
        if (flag === 0) {
          let book = {
            id: enteredId,
            title: enteredTitle,
            author: enteredAuthor,
            price: enteredPrice
          }
           let newBooks = [...books]
          newBooks.push(book);
          setBooks(newBooks); 
        } 
    */

    let book = {
      id: enteredId,
      title: enteredTitle,
      author: enteredAuthor,
      price: enteredPrice
    }
    let flag = 0;
    books.filter(
      (element) => {
        if ( book.id == element.id)
          return flag = 1;
      }
    )

    fetch('http://localhost:8000/books', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(book)
    })
      .then((book) => {
        if (flag == 0) {
          let newBooks = [...books]
          newBooks.push(book);
          setBooks(newBooks);
          setEnteredId("");
          setEnteredTitle("");
          setEnteredAuthor("");
          setEnteredPrice("");
        }

      })
  }

  return (
    <div className='main-container'>
      <div className='top-section'>
        <img src="book-logo.png" alt="Logo" />
        <h1 className='heading'>Book Management System</h1>

      </div>
      <AddBook key={enteredId} handleSubmit={handleSubmit} enteredId={enteredId} enteredTitle={enteredTitle} enteredAuthor={enteredAuthor} enteredPrice={enteredPrice} setEnteredId={setEnteredId} setEnteredTitle={setEnteredTitle} setEnteredAuthor={setEnteredAuthor} setEnteredPrice={setEnteredPrice} />
      {
        books && books.map((element) => {
          return <Book key={element.id} id={element.id} title={element.title} author={element.author} price={element.price} handleRemove={handleRemove} books={books} setBooks={setBooks} />
        })
      }
    </div>
  );
}

export default App;
