import { useState } from 'react'
import './Book.css'

function Book(props) {
    let [isUpdating, setIdUpdating] = useState(false);
    let [updatedTitle, setUpdatedTitle] = useState(props.title)
    let [updatedAuthor, setUpdatedAuthor] = useState(props.author)
    let [updatedPrice, setUpdatedPrice] = useState(props.price)

    function handleTitleChnage(event) {
        setUpdatedTitle(event.target.value)
    }
    function handleAuthorChnage(event) {
        setUpdatedAuthor(event.target.value)
    }
    function handlePriceChnage(event) {
        setUpdatedPrice(event.target.value)
    }

    function handleUpdate() {
        let book
        if (isUpdating) {
            book = {
                id: props.id,
                title: updatedTitle,
                author: updatedAuthor,
                price: updatedPrice
            }
            fetch(`http://localhost:8000/books/${props.id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(book)
            })
                .then(() => {
                    let newBooks = [...props.books];
                    newBooks.map(
                        (element, idx) => {
                            if (element.id === book.id) {
                                newBooks.splice(idx, 1, book);
                            }
                        }
                    )

                    props.setBooks(newBooks);
                    setIdUpdating(false);
                })
        }
        else {
            setIdUpdating(true);
        }
    }

    return (
        <div className="container">
            <div class="input-group flex-nowrap">
                <span class="input-group-text " id="addon-wrapping">{props.id} </span>

                {/* </div>
            <div class="input-group flex-nowrap"> */}
                {
                    isUpdating ? <input type="text" class="form-control" aria-label="Username" aria-describedby="addon-wrapping" value={updatedTitle} onChange={handleTitleChnage} /> : <span class="input-group-text" id="addon-wrapping">{props.title} </span>
                }
                {/* </div>
            <div class="input-group flex-nowrap"> */}
                {
                    isUpdating ? <input type="text" class="form-control" aria-label="Username" aria-describedby="addon-wrapping" value={updatedAuthor} onChange={handleAuthorChnage} /> : <span class="input-group-text" id="addon-wrapping">{props.author} </span>
                }
                {/* </div>

            <div class="input-group flex-nowrap"> */}
                {
                    isUpdating ? <input type="text" class="form-control" aria-label="Username" aria-describedby="addon-wrapping" value={updatedPrice} onChange={handlePriceChnage} /> : <span class="input-group-text" id="addon-wrapping">{props.price} </span>
                }

                <button id='btnRemove' onClick={() => { props.handleRemove(props.id) }}>Remove</button>
                <button id='btnUpdate' onClick={handleUpdate}>
                    {
                        isUpdating ? "Save" : "Update"
                    }
                </button>
            </div>


        </div>



    )
}

export default Book;







